INSERT INTO `didyouknow` (`dyk_id`, `didyouknow`, `user_mail`, `time`) VALUES
(4, '<p>दुनिया भर में 11% लोग Left handed होते है.</p>', NULL, '2019-04-04 13:01:18'),
(5, '<p>अगस्त माह में सबसे ज्यादा बच्चों का जन्म होता है.</p>', NULL, '2019-04-04 13:01:39'),
(6, '<p>अगर हमारे मुहँ में लार नही होती तो हम किसी भी चीज को taste नही कर पाते.</p>', NULL, '2019-04-04 13:01:55'),
(7, '<p>औसतन मानव 7 मिनट में सो जाते है.</p>', NULL, '2019-04-04 13:02:13'),
(8, '<p>छींकते समय आपके दिल की धड़कन कुछ मिलीसेकेंड के लिए रुक जाती है</p>', NULL, '2019-04-04 13:02:59'),
(9, '<p>एक भालू के कुल 42 दांत होते है.</p>', NULL, '2019-04-04 13:03:05'),
(10, '<p>दो केलों में 90 मिनट तक कड़े व्यायाम करने के लिए ऊर्जा होती है। केले को खुशी देने वाला फल भी कहा जाता है</p>', NULL, '2019-04-04 13:03:12'),
(11, '<p>हाथ की उँगलियों के नाख़ून, पैरों की उँगलियों के नाखूनों के मुकाबले चार गुना ज्यादा तेजी से बढ़ते हैं</p>', NULL, '2019-04-04 13:03:14'),
